function submitAnswers(){var total=1,score=0,q1=document.forms.quizForm.q1.value;if(null==q1||""==q1)return alert("You missed question 1"),!1;for(i=1;i<=total;i++)if(null==eval("q"+i)||""==eval("q"+i))return alert("You missed question "+i),!1;var answer=["b"];for(i=1;i<=total;i++)eval("q"+i)==answer[i-1]&&score++;var results=document.getElementById("results");return results.innerHTML="<h3>You scored <span>"+score+"</span> out of <span>"+total+"</span></h3>",alert("You scored "+score+" out of "+total),!1}